import React, { useState } from 'react';
import { ShoppingCart, Plus, Minus, Calendar, MapPin, ChevronDown, ChevronUp } from 'lucide-react';

const OrderForm = () => {
  // Sample menu data based on your structure
  const menuData = {
    "name": "14 Saturday Menu",
    "menu_date": "2025-06-17",
    "items": [
      {
        "catalog_item_id": 2008,
        "item_name": "Combo3",
        "description": "Only Starters",
        "price": "18.0",
        "category": null,
        "is_combo": false,
        "combo_items": null
      },
      {
        "catalog_item_id": 2007,
        "item_name": "Veg Combo",
        "description": "Starter and Biryani",
        "price": "12.0",
        "category": null,
        "is_combo": false,
        "combo_items": null
      }
    ],
    "orderlink": "/order/M20250617",
    "active": true,
    "menu_id": "M20250617",
    "caterer_id": 1001,
    "created_at": "2025-06-16T11:48:41.614164+01:00"
  };

  const [cart, setCart] = useState({});
  const [expandedItems, setExpandedItems] = useState({});
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    phone: '',
    email: '',
    address: '',
    notes: ''
  });

  const toggleItemExpansion = (itemId) => {
    setExpandedItems(prev => ({
      ...prev,
      [itemId]: !prev[itemId]
    }));
  };

  const addToCart = (item) => {
    setCart(prev => ({
      ...prev,
      [item.catalog_item_id]: {
        ...item,
        quantity: (prev[item.catalog_item_id]?.quantity || 0) + 1
      }
    }));
  };

  const removeFromCart = (itemId) => {
    setCart(prev => {
      const newCart = { ...prev };
      if (newCart[itemId]?.quantity > 1) {
        newCart[itemId].quantity -= 1;
      } else {
        delete newCart[itemId];
      }
      return newCart;
    });
  };

  const getTotalPrice = () => {
    return Object.values(cart).reduce((total, item) => {
      return total + (parseFloat(item.price) * item.quantity);
    }, 0).toFixed(2);
  };

  const getTotalItems = () => {
    return Object.values(cart).reduce((total, item) => total + item.quantity, 0);
  };

  const handleSubmitOrder = () => {
    // Validate required fields
    if (!customerInfo.name || !customerInfo.phone || !customerInfo.address) {
      alert('Please fill in all required fields (Name, Phone, Address)');
      return;
    }

    const orderData = {
      menu_id: menuData.menu_id,
      caterer_id: menuData.caterer_id,
      customer_info: customerInfo,
      items: Object.values(cart),
      total_amount: getTotalPrice(),
      order_date: new Date().toISOString(),
      menu_date: menuData.menu_date
    };

    console.log('Order submitted:', orderData);
    alert(`Order placed successfully! Total: Â£${getTotalPrice()}`);

    // Reset form
    setCart({});
    setCustomerInfo({
      name: '',
      phone: '',
      email: '',
      address: '',
      notes: ''
    });
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-GB', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  return (
    <div className="max-w-4xl mx-auto p-6 bg-gray-50 min-h-screen">
      {/* Header */}
      <div className="bg-white rounded-lg shadow-md p-6 mb-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-gray-800">{menuData.name}</h1>
            <div className="flex items-center mt-2 text-gray-600">
              <Calendar className="w-4 h-4 mr-2" />
              <span>{formatDate(menuData.menu_date)}</span>
            </div>
            <p className="text-sm text-gray-500 mt-1">Menu ID: {menuData.menu_id}</p>
          </div>
          <div className="text-right">
            <div className="bg-blue-100 text-blue-800 px-3 py-1 rounded-full text-sm font-medium">
              {menuData.active ? 'Available' : 'Unavailable'}
            </div>
          </div>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Menu Items */}
        <div className="lg:col-span-2">
          <div className="bg-white rounded-lg shadow-md p-6">
            <h2 className="text-2xl font-bold text-gray-800 mb-4">Menu Items</h2>
            <div className="space-y-4">
              {menuData.items.map((item) => (
                <div key={item.catalog_item_id} className="border border-gray-200 rounded-lg overflow-hidden hover:shadow-md transition-shadow">
                  {/* Item Header - Always Visible */}
                  <div className="p-4 bg-white">
                    <div className="flex justify-between items-center">
                      <div className="flex-1">
                        <div className="flex items-center">
                          <h3 className="text-lg font-semibold text-gray-800">{item.item_name}</h3>
                          {item.is_combo && (
                            <span className="ml-2 bg-orange-100 text-orange-800 px-2 py-1 rounded text-xs font-medium">
                              COMBO
                            </span>
                          )}
                        </div>
                        <div className="flex items-center justify-between mt-2">
                          <span className="text-2xl font-bold text-green-600">Â£{item.price}</span>
                          <div className="flex items-center space-x-2">
                            <button
                              onClick={() => toggleItemExpansion(item.catalog_item_id)}
                              className="flex items-center text-blue-600 hover:text-blue-800 text-sm font-medium transition-colors"
                            >
                              Details
                              {expandedItems[item.catalog_item_id] ? (
                                <ChevronUp className="w-4 h-4 ml-1" />
                              ) : (
                                <ChevronDown className="w-4 h-4 ml-1" />
                              )}
                            </button>
                          </div>
                        </div>
                      </div>

                      {/* Cart Controls */}
                      <div className="flex items-center space-x-2 ml-4">
                        {cart[item.catalog_item_id] && (
                          <>
                            <button
                              onClick={() => removeFromCart(item.catalog_item_id)}
                              className="bg-red-500 hover:bg-red-600 text-white p-1 rounded-full transition-colors"
                            >
                              <Minus className="w-4 h-4" />
                            </button>
                            <span className="bg-gray-100 px-3 py-1 rounded font-medium min-w-[2rem] text-center">
                              {cart[item.catalog_item_id].quantity}
                            </span>
                          </>
                        )}
                        <button
                          onClick={() => addToCart(item)}
                          className="bg-blue-500 hover:bg-blue-600 text-white p-1 rounded-full transition-colors"
                        >
                          <Plus className="w-4 h-4" />
                        </button>
                      </div>
                    </div>
                  </div>

                  {/* Expandable Details Section */}
                  {expandedItems[item.catalog_item_id] && (
                    <div className="border-t border-gray-200 bg-gray-50 p-4 animate-in slide-in-from-top-2 duration-200">
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div>
                          <h4 className="font-medium text-gray-800 mb-2">Description</h4>
                          <p className="text-gray-600">{item.description}</p>
                        </div>

                        <div>
                          <h4 className="font-medium text-gray-800 mb-2">Item Details</h4>
                          <div className="space-y-1 text-sm text-gray-600">
                            <div className="flex justify-between">
                              <span>Item ID:</span>
                              <span className="font-mono">{item.catalog_item_id}</span>
                            </div>
                            <div className="flex justify-between">
                              <span>Price:</span>
                              <span className="font-semibold text-green-600">Â£{item.price}</span>
                            </div>
                            <div className="flex justify-between">
                              <span>Type:</span>
                              <span>{item.is_combo ? 'Combo Meal' : 'Single Item'}</span>
                            </div>
                            {item.category && (
                              <div className="flex justify-between">
                                <span>Category:</span>
                                <span>{item.category}</span>
                              </div>
                            )}
                          </div>
                        </div>
                      </div>

                      {/* Combo Items Section */}
                      {item.is_combo && item.combo_items && (
                        <div className="mt-4 pt-4 border-t border-gray-200">
                          <h4 className="font-medium text-gray-800 mb-2">Combo Includes</h4>
                          <div className="grid grid-cols-1 sm:grid-cols-2 gap-2">
                            {item.combo_items.map((comboItem, index) => (
                              <div key={index} className="bg-white p-2 rounded border border-gray-200">
                                <span className="text-sm font-medium text-gray-700">{comboItem.name}</span>
                                {comboItem.description && (
                                  <p className="text-xs text-gray-500 mt-1">{comboItem.description}</p>
                                )}
                              </div>
                            ))}
                          </div>
                        </div>
                      )}

                      {/* Quick Add Section */}
                      <div className="mt-4 pt-4 border-t border-gray-200">
                        <div className="flex items-center justify-between">
                          <span className="text-sm text-gray-600">Add to cart:</span>
                          <div className="flex items-center space-x-2">
                            <button
                              onClick={() => addToCart(item)}
                              className="bg-blue-500 hover:bg-blue-600 text-white px-3 py-1 rounded text-sm transition-colors"
                            >
                              Add 1
                            </button>
                            <button
                              onClick={() => {
                                for(let i = 0; i < 2; i++) addToCart(item);
                              }}
                              className="bg-green-500 hover:bg-green-600 text-white px-3 py-1 rounded text-sm transition-colors"
                            >
                              Add 2
                            </button>
                          </div>
                        </div>
                      </div>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>

        {/* Order Summary & Customer Info */}
        <div className="space-y-6">
          {/* Cart Summary */}
          <div className="bg-white rounded-lg shadow-md p-6 sticky top-6">
            <div className="flex items-center mb-4">
              <ShoppingCart className="w-5 h-5 mr-2" />
              <h3 className="text-lg font-semibold">Order Summary</h3>
            </div>

            {Object.keys(cart).length === 0 ? (
              <p className="text-gray-500 text-center py-4">Your cart is empty</p>
            ) : (
              <>
                <div className="space-y-3 mb-4">
                  {Object.values(cart).map((item) => (
                    <div key={item.catalog_item_id} className="flex justify-between items-center text-sm">
                      <div>
                        <span className="font-medium">{item.item_name}</span>
                        <span className="text-gray-500 ml-2">x{item.quantity}</span>
                      </div>
                      <span className="font-medium">Â£{(parseFloat(item.price) * item.quantity).toFixed(2)}</span>
                    </div>
                  ))}
                </div>
                <div className="border-t pt-3">
                  <div className="flex justify-between items-center font-bold text-lg">
                    <span>Total ({getTotalItems()} items)</span>
                    <span className="text-green-600">Â£{getTotalPrice()}</span>
                  </div>
                </div>
              </>
            )}
          </div>

          {/* Customer Information Form */}
          {Object.keys(cart).length > 0 && (
            <div className="bg-white rounded-lg shadow-md p-6">
              <h3 className="text-lg font-semibold mb-4">Customer Information</h3>
              <div className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Full Name *
                  </label>
                  <input
                    type="text"
                    required
                    value={customerInfo.name}
                    onChange={(e) => setCustomerInfo(prev => ({ ...prev, name: e.target.value }))}
                    className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:border-blue-500"
                    placeholder="Enter your name"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Phone Number *
                  </label>
                  <input
                    type="tel"
                    required
                    value={customerInfo.phone}
                    onChange={(e) => setCustomerInfo(prev => ({ ...prev, phone: e.target.value }))}
                    className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:border-blue-500"
                    placeholder="Enter phone number"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Email
                  </label>
                  <input
                    type="email"
                    value={customerInfo.email}
                    onChange={(e) => setCustomerInfo(prev => ({ ...prev, email: e.target.value }))}
                    className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:border-blue-500"
                    placeholder="Enter email address"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Delivery Address *
                  </label>
                  <textarea
                    required
                    value={customerInfo.address}
                    onChange={(e) => setCustomerInfo(prev => ({ ...prev, address: e.target.value }))}
                    className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:border-blue-500"
                    rows="3"
                    placeholder="Enter delivery address"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Special Notes
                  </label>
                  <textarea
                    value={customerInfo.notes}
                    onChange={(e) => setCustomerInfo(prev => ({ ...prev, notes: e.target.value }))}
                    className="w-full border border-gray-300 rounded-lg px-3 py-2 focus:outline-none focus:border-blue-500"
                    rows="2"
                    placeholder="Any special instructions..."
                  />
                </div>

                <button
                  onClick={handleSubmitOrder}
                  className="w-full bg-green-600 hover:bg-green-700 text-white font-medium py-3 px-4 rounded-lg transition-colors flex items-center justify-center"
                >
                  <ShoppingCart className="w-5 h-5 mr-2" />
                  Place Order - Â£{getTotalPrice()}
                </button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default OrderForm;
