import api from './api';

export const menuService = {
  getMenuItems: async (params = {}) => {
    const response = await api.get('/menu/items', { params });
    return response.data;
  },

  getMyMenuItems: async () => {
    const response = await api.get('/menu/items/my');
    return response.data;
  },

  getMenuItem: async (menuId) => {
    const response = await api.get(`/menu/items/${menuId}`);
    return response.data;
  },

  createMenuItem: async (menuData) => {
    const response = await api.post('/menu/items', menuData);
    return response.data;
  },

  updateMenuItem: async (menuId, menuData) => {
    const response = await api.put(`/menu/items/${menuId}`, menuData);
    return response.data;
  },

  deleteMenuItem: async (menuId) => {
    const response = await api.delete(`/menu/items/${menuId}`);
    return response.data;
  },
  // 
  // Catalog methods
  getCatalogItems: async (params = {}) => {
    try {
      const response = await api.get('/menu/catalog', { params });
      return response.data;
    } catch (error) {
      console.error('Error loading catalog items:', error);
      throw error;
    }
  },

  createCatalogItem: async (catalogData) => {
    try {
      const response = await api.post('/menu/catalog', catalogData);
      return response.data;
    } catch (error) {
      console.error('Error creating catalog item:', error);
      throw error;
    }
  },

  updateCatalogItem: async (itemId, catalogData) => {
    try {
      const response = await api.put(`/menu/catalog/${itemId}`, catalogData);
      return response.data;
    } catch (error) {
      console.error('Error updating catalog item:', error);
      throw error;
    }
  },

  deleteCatalogItem: async (itemId) => {
    try {
      const response = await api.delete(`/menu/catalog/${itemId}`);
      return response.data;
    } catch (error) {
      console.error('Error deleting catalog item:', error);
      throw error;
    }
  },

  getCatalogCategories: async () => {
    try {
      const response = await api.get('/menu/catalog/categories');
      return response.data;
    } catch (error) {
      console.error('Error loading categories:', error);
      throw error;
    }
  }
};
