import React, { useState, useEffect } from 'react';
import { Calendar, Search, Filter, DollarSign, Clock, User, Phone, MapPin } from 'lucide-react';


// Mock API service (replace with your actual API)
const api = {
  getPaymentsByMenuDate: async (menuDate) => {
    // This would be your actual API call
    // const response = await fetch(`/api/payments/by-menu-date?menu_date=${menuDate}`);
    // return response.json();

    // Mock data for demonstration
    return new Promise(resolve => {
      setTimeout(() => {
        resolve([
          {
            payment_id: 1,
            order_id: 101,
            customer_name: "John Doe",
            customer_phone: "+1234567890",
            customer_address: { street: "123 Main St", city: "New York", zip: "10001" },
            menu_date: menuDate,
            order_date: "2025-07-01T10:30:00Z",
            total: 45.50,
            payment_method: "offline",
            payment_status: "completed",
            payment_date: "2025-07-01T14:20:00Z",
            payment_amount: 45.50,
            items: [
              { name: "Chicken Curry", quantity: 2, price: 15.00 },
              { name: "Rice", quantity: 2, price: 7.75 }
            ]
          },
          {
            payment_id: 2,
            order_id: 102,
            customer_name: "Jane Smith",
            customer_phone: "+1987654321",
            customer_address: { street: "456 Oak Ave", city: "Boston", zip: "02101" },
            menu_date: menuDate,
            order_date: "2025-07-01T11:15:00Z",
            total: 32.25,
            payment_method: "offline",
            payment_status: "completed",
            payment_date: "2025-07-01T15:45:00Z",
            payment_amount: 32.25,
            items: [
              { name: "Vegetable Biryani", quantity: 1, price: 18.00 },
              { name: "Raita", quantity: 1, price: 4.25 },
              { name: "Papad", quantity: 2, price: 5.00 }
            ]
          },
          {
            payment_id: 3,
            order_id: 103,
            customer_name: "Mike Johnson",
            customer_phone: "+1122334455",
            customer_address: { street: "789 Pine Rd", city: "Chicago", zip: "60601" },
            menu_date: menuDate,
            order_date: "2025-07-01T12:00:00Z",
            total: 28.75,
            payment_method: "offline",
            payment_status: "pending",
            payment_date: null,
            payment_amount: 0,
            items: [
              { name: "Dal Tadka", quantity: 1, price: 12.00 },
              { name: "Naan", quantity: 3, price: 9.00 },
              { name: "Lassi", quantity: 1, price: 7.75 }
            ]
          }
        ]);
      }, 500);
    });
  }
};

const ManagePayments = () => {
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(false);
  const [selectedMenuDate, setSelectedMenuDate] = useState(new Date().toISOString().split('T')[0]);
  const [searchTerm, setSearchTerm] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');

  useEffect(() => {
    fetchPayments();
  }, [selectedMenuDate]);

  const fetchPayments = async () => {
    setLoading(true);
    try {
      const data = await api.getPaymentsByMenuDate(selectedMenuDate);
      setPayments(data);
    } catch (error) {
      console.error('Error fetching payments:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredPayments = payments.filter(payment => {
    const matchesSearch = payment.customer_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         payment.customer_phone.includes(searchTerm) ||
                         payment.order_id.toString().includes(searchTerm);

    const matchesStatus = statusFilter === 'all' || payment.payment_status === statusFilter;

    return matchesSearch && matchesStatus;
  });

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    return new Date(dateString).toLocaleString();
  };

  const getStatusBadge = (status) => {
    const statusColors = {
      completed: 'bg-green-100 text-green-800',
      pending: 'bg-yellow-100 text-yellow-800',
      failed: 'bg-red-100 text-red-800'
    };

    return (
      <span className={`px-2 py-1 rounded-full text-xs font-medium ${statusColors[status] || 'bg-gray-100 text-gray-800'}`}>
        {status}
      </span>
    );
  };

  const totalAmount = filteredPayments.reduce((sum, payment) => sum + payment.payment_amount, 0);
  const completedPayments = filteredPayments.filter(p => p.payment_status === 'completed').length;

  return (
    <div className="min-h-screen bg-gray-50 p-6">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Payment Management</h1>
          <p className="text-gray-600">Manage offline payments filtered by menu date</p>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <DollarSign className="h-8 w-8 text-green-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Total Amount</p>
                <p className="text-2xl font-bold text-gray-900">{formatCurrency(totalAmount)}</p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <Clock className="h-8 w-8 text-blue-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Completed Payments</p>
                <p className="text-2xl font-bold text-gray-900">{completedPayments}</p>
              </div>
            </div>
          </div>

          <div className="bg-white rounded-lg shadow p-6">
            <div className="flex items-center">
              <User className="h-8 w-8 text-purple-600" />
              <div className="ml-4">
                <p className="text-sm font-medium text-gray-600">Total Orders</p>
                <p className="text-2xl font-bold text-gray-900">{filteredPayments.length}</p>
              </div>
            </div>
          </div>
        </div>

        {/* Filters */}
        <div className="bg-white rounded-lg shadow mb-6 p-6">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Menu Date
              </label>
              <div className="relative">
                <Calendar className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                <input
                  type="date"
                  value={selectedMenuDate}
                  onChange={(e) => setSelectedMenuDate(e.target.value)}
                  className="pl-10 w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Search
              </label>
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                <input
                  type="text"
                  placeholder="Customer name, phone, or order ID"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10 w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Payment Status
              </label>
              <div className="relative">
                <Filter className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                <select
                  value={statusFilter}
                  onChange={(e) => setStatusFilter(e.target.value)}
                  className="pl-10 w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                >
                  <option value="all">All Status</option>
                  <option value="completed">Completed</option>
                  <option value="pending">Pending</option>
                  <option value="failed">Failed</option>
                </select>
              </div>
            </div>

            <div className="flex items-end">
              <button
                onClick={fetchPayments}
                disabled={loading}
                className="w-full bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:opacity-50"
              >
                {loading ? 'Loading...' : 'Refresh'}
              </button>
            </div>
          </div>
        </div>

        {/* Payments Table */}
        <div className="bg-white rounded-lg shadow overflow-hidden">
          <div className="px-6 py-4 border-b border-gray-200">
            <h2 className="text-lg font-medium text-gray-900">
              Offline Payments ({filteredPayments.length})
            </h2>
          </div>

          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Order Details
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Customer
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Amount
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Payment Status
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Payment Date
                  </th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                    Items
                  </th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {loading ? (
                  <tr>
                    <td colSpan="6" className="px-6 py-4 text-center text-gray-500">
                      Loading payments...
                    </td>
                  </tr>
                ) : filteredPayments.length === 0 ? (
                  <tr>
                    <td colSpan="6" className="px-6 py-4 text-center text-gray-500">
                      No payments found for the selected date
                    </td>
                  </tr>
                ) : (
                  filteredPayments.map((payment) => (
                    <tr key={payment.payment_id} className="hover:bg-gray-50">
                      <td className="px-6 py-4 whitespace-nowrap">
                        <div>
                          <div className="text-sm font-medium text-gray-900">
                            Order #{payment.order_id}
                          </div>
                          <div className="text-sm text-gray-500">
                            {formatDate(payment.order_date)}
                          </div>
                        </div>
                      </td>

                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="flex items-center">
                          <div>
                            <div className="flex items-center text-sm font-medium text-gray-900">
                              <User className="h-4 w-4 mr-1" />
                              {payment.customer_name}
                            </div>
                            <div className="flex items-center text-sm text-gray-500">
                              <Phone className="h-4 w-4 mr-1" />
                              {payment.customer_phone}
                            </div>
                            <div className="flex items-center text-sm text-gray-500">
                              <MapPin className="h-4 w-4 mr-1" />
                              {payment.customer_address.city}
                            </div>
                          </div>
                        </div>
                      </td>

                      <td className="px-6 py-4 whitespace-nowrap">
                        <div className="text-sm font-medium text-gray-900">
                          {formatCurrency(payment.total)}
                        </div>
                        <div className="text-sm text-gray-500">
                          Paid: {formatCurrency(payment.payment_amount)}
                        </div>
                      </td>

                      <td className="px-6 py-4 whitespace-nowrap">
                        {getStatusBadge(payment.payment_status)}
                      </td>

                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                        {formatDate(payment.payment_date)}
                      </td>

                      <td className="px-6 py-4">
                        <div className="text-sm text-gray-900">
                          {payment.items.map((item, index) => (
                            <div key={index} className="flex justify-between">
                              <span>{item.name} x{item.quantity}</span>
                              <span>{formatCurrency(item.price * item.quantity)}</span>
                            </div>
                          ))}
                        </div>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ManagePayments;
