import api from './api';

export const sendNotifications= {
  sendInvoicebyEmail: async (email_request) => {
    const response = await api.post('/notifications/send-email',email_request);
    return response.data;
  },

  sendInvoicebySMS: async (sms_request,background_tasks) => {
    const response = await api.get('/notifications/send-sms', sms_request);
    return response.data;
  },
}
