import React from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { ArrowLeft, ShoppingCart, Calendar, Clock } from 'lucide-react';

const OrderFormHeader = () => {
  const navigate = useNavigate();
  const { menuId } = useParams();

  const handleGoBack = () => {
    navigate(-1); // Go back to previous page
  };

  const handleGoHome = () => {
    navigate('/');
  };

  return (
    <header className="bg-white shadow-md border-b border-gray-200">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">

          {/* Left Section - Back Navigation */}
          <div className="flex items-center space-x-4">
            <button
              onClick={handleGoBack}
              className="flex items-center text-gray-600 hover:text-gray-900 transition-colors"
            >
              <ArrowLeft className="w-5 h-5 mr-2" />
              <span className="hidden sm:inline">Back</span>
            </button>

            <div className="h-6 border-l border-gray-300"></div>

            <button
              onClick={handleGoHome}
              className="text-2xl font-bold text-blue-600 hover:text-blue-700 transition-colors"
            >
              Caterer Name {/* Replace with actual caterer name */}
            </button>
          </div>

          {/* Center Section - Order Info */}
          <div className="hidden md:flex items-center space-x-6">
            <div className="flex items-center text-gray-600">
              <Calendar className="w-4 h-4 mr-2" />
              <span className="text-sm">Menu: {menuId}</span>
            </div>

            <div className="flex items-center text-gray-600">
              <Clock className="w-4 h-4 mr-2" />
              <span className="text-sm">Order in Progress</span>
            </div>
          </div>

          {/* Right Section - Order Status */}
          <div className="flex items-center space-x-4">
            <div className="hidden sm:flex items-center bg-blue-50 text-blue-700 px-3 py-1 rounded-full text-sm font-medium">
              <ShoppingCart className="w-4 h-4 mr-2" />
              <span>Placing Order</span>
            </div>

            {/* Mobile menu button or additional actions */}
            <div className="flex items-center space-x-2">
              <div className="w-8 h-8 bg-blue-100 rounded-full flex items-center justify-center">
                <span className="text-blue-600 font-medium text-sm">1</span>
              </div>
            </div>
          </div>
        </div>

        {/* Mobile Order Info */}
        <div className="md:hidden pb-3 border-t border-gray-100 pt-3">
          <div className="flex items-center justify-between text-sm text-gray-600">
            <div className="flex items-center">
              <Calendar className="w-4 h-4 mr-2" />
              <span>Menu: {menuId}</span>
            </div>
            <div className="flex items-center">
              <Clock className="w-4 h-4 mr-2" />
              <span>Order in Progress</span>
            </div>
          </div>
        </div>
      </div>
    </header>
  );
};

export default OrderFormHeader;
