import React, { useState } from 'react';
import { Package, Edit, Trash2, Plus, Search, Filter, DollarSign, Clock, Star } from 'lucide-react';

const VerticalCatalogGrid = ({ catalogItems }) => {
  // Mock data - replace with your actual data
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');

  // Get unique categories
  const categories = [...new Set(catalogItems.map(item => item.category))].filter(Boolean);

  // Filter items
  const filteredItems = catalogItems.filter(item => {
    const matchesSearch = item.item_name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         (item.description && item.description.toLowerCase().includes(searchTerm.toLowerCase()));
    const matchesCategory = !selectedCategory || item.category === selectedCategory;
    return matchesSearch && matchesCategory;
  });

  const handleEdit = (item) => {
    console.log('Edit item:', item);
  };

  const handleDelete = (item) => {
    console.log('Delete item:', item);
  };

  const handleAddToMenu = (item) => {
    console.log('Add to menu:', item);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-6xl mx-auto p-6">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Menu Catalog</h1>
          <p className="text-gray-600">Browse and manage your menu items and combo deals</p>
        </div>

        {/* Search and Filter Controls */}
        <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
          <div className="flex flex-col lg:flex-row gap-4">
            {/* Search */}
            <div className="relative flex-1">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
              <input
                type="text"
                placeholder="Search menu items..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500 focus:border-orange-500"
              />
            </div>

            {/* Category Filter */}
            <div className="relative">
              <Filter className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
              <select
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="pl-10 pr-8 py-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-orange-500 focus:border-orange-500 min-w-[200px] appearance-none bg-white"
              >
                <option value="">All Categories</option>
                {categories.map(category => (
                  <option key={category} value={category}>{category}</option>
                ))}
              </select>
            </div>

            {/* Add Buttons */}
            <div className="flex gap-2">
              <button className="inline-flex items-center px-4 py-3 bg-orange-600 text-white rounded-lg hover:bg-orange-700 transition-colors">
                <Plus className="h-4 w-4 mr-2" />
                Add Item
              </button>
              <button className="inline-flex items-center px-4 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors">
                <Package className="h-4 w-4 mr-2" />
                Add Combo
              </button>
            </div>
          </div>
        </div>

        {/* Catalog Items - Grouped Layout */}
        <div className="space-y-8">
          {/* Combo Deals Section */}
          {filteredItems.filter(item => item.is_combo).length > 0 && (
            <div>
              <div className="flex items-center justify-between mb-6">
                <div className="flex items-center">
                  <div className="w-1 h-8 bg-blue-600 rounded-full mr-4"></div>
                  <div>
                    <h2 className="text-2xl font-bold text-gray-900">Combo Deals</h2>
                    <p className="text-blue-600 text-sm font-medium">
                      {filteredItems.filter(item => item.is_combo).length} combo{filteredItems.filter(item => item.is_combo).length !== 1 ? 's' : ''} available
                    </p>
                  </div>
                </div>
                <span className="px-3 py-1 bg-blue-100 text-blue-700 text-sm font-medium rounded-full">
                  Special Offers
                </span>
              </div>

              <div className="space-y-4">
                {filteredItems
                  .filter(item => item.is_combo)
                  .map((item) => (
                    <CatalogItemCard
                      key={item.menu_item_id}
                      item={item}
                      onEdit={handleEdit}
                      onDelete={handleDelete}
                      onAddToMenu={handleAddToMenu}
                    />
                  ))}
              </div>
            </div>
          )}

          {/* Regular Items Section */}
          {filteredItems.filter(item => !item.is_combo).length > 0 && (
            <div>
              <div className="flex items-center justify-between mb-6">
                <div className="flex items-center">
                  <div className="w-1 h-8 bg-orange-600 rounded-full mr-4"></div>
                  <div>
                    <h2 className="text-2xl font-bold text-gray-900">Regular Menu Items</h2>
                    <p className="text-orange-600 text-sm font-medium">
                      {filteredItems.filter(item => !item.is_combo).length} item{filteredItems.filter(item => !item.is_combo).length !== 1 ? 's' : ''} available
                    </p>
                  </div>
                </div>
                <span className="px-3 py-1 bg-orange-100 text-orange-700 text-sm font-medium rounded-full">
                  Individual Items
                </span>
              </div>

              <div className="space-y-4">
                {filteredItems
                  .filter(item => !item.is_combo)
                  .map((item) => (
                    <CatalogItemCard
                      key={item.menu_item_id}
                      item={item}
                      onEdit={handleEdit}
                      onDelete={handleDelete}
                      onAddToMenu={handleAddToMenu}
                    />
                  ))}
              </div>
            </div>
          )}
        </div>

        {/* Empty State */}
        {filteredItems.length === 0 && (
          <div className="text-center py-12 bg-white rounded-lg shadow-sm">
            <Package className="h-16 w-16 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">
              No items found
            </h3>
            <p className="text-gray-600 mb-4">
              {searchTerm || selectedCategory 
                ? 'Try adjusting your search or filter criteria.'
                : 'Start building your catalog by adding your first item.'
              }
            </p>
            <div className="flex gap-3 justify-center">
              <button className="inline-flex items-center px-4 py-2 bg-orange-600 text-white rounded-lg hover:bg-orange-700 transition-colors">
                <Plus className="h-4 w-4 mr-2" />
                Add Your First Item
              </button>
              <button className="inline-flex items-center px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors">
                <Package className="h-4 w-4 mr-2" />
                Create Your First Combo
              </button>
            </div>
          </div>
        )}

        {/* Summary Statistics */}
        {filteredItems.length > 0 && (
          <div className="mt-8 bg-white rounded-lg shadow-sm p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Catalog Summary</h3>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="text-center">
                <div className="text-3xl font-bold text-blue-600">
                  {filteredItems.filter(item => item.is_combo).length}
                </div>
                <div className="text-sm text-gray-600 mt-1">Combo Deals</div>
              </div>
              <div className="text-center">
                <div className="text-3xl font-bold text-orange-600">
                  {filteredItems.filter(item => !item.is_combo).length}
                </div>
                <div className="text-sm text-gray-600 mt-1">Regular Items</div>
              </div>
              <div className="text-center">
                <div className="text-3xl font-bold text-gray-900">
                  {categories.length}
                </div>
                <div className="text-sm text-gray-600 mt-1">Categories</div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

const CatalogItemCard = ({ item, onEdit, onDelete, onAddToMenu }) => {
  const [showDetails, setShowDetails] = useState(false);

  return (
    <div className={`bg-white rounded-lg shadow-sm border transition-all duration-200 hover:shadow-md ${
      item.is_combo ? 'border-l-4 border-l-blue-500' : 'border-l-4 border-l-orange-500'
    }`}>
      <div className="p-4">
        <div className="flex items-center justify-between">
          {/* Left Side - Item Info */}
          <div className="flex-1 min-w-0 mr-4">
            <div className="flex items-center gap-2 mb-1">
              <h3 className="text-lg font-bold text-gray-900 truncate">
                {item.item_name}
              </h3>

              {/* Compact Badges */}
              {item.is_combo ? (
                <span className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-blue-100 text-blue-800">
                  COMBO
                </span>
              ) : (
                <span className="inline-flex items-center px-2 py-0.5 rounded text-xs font-medium bg-orange-100 text-orange-800">
                  ITEM
                </span>
              )}
            </div>

            {/* Category and Description in one line */}
            <div className="flex items-center gap-2 mb-2">
              {item.category && (
                <span className="inline-flex items-center px-2 py-0.5 rounded text-xs bg-gray-100 text-gray-600">
                  {item.category}
                </span>
              )}
              {item.description && (
                <span className="text-sm text-gray-600 truncate">
                  {item.description}
                </span>
              )}
            </div>

            {/* Combo Items Preview - Compact */}
            {item.is_combo && item.combo_items && (
              <div className="flex flex-wrap gap-1 mb-2">
                {item.combo_items.slice(0, 2).map((comboItem, index) => (
                  <span
                    key={index}
                    className="inline-flex items-center px-1.5 py-0.5 bg-blue-50 text-blue-700 text-xs rounded"
                  >
                    {comboItem.item_name} ×{comboItem.quantity}
                  </span>
                ))}
                {item.combo_items.length > 2 && (
                  <button
                    onClick={() => setShowDetails(!showDetails)}
                    className="inline-flex items-center px-1.5 py-0.5 bg-gray-50 text-gray-600 text-xs rounded hover:bg-gray-100"
                  >
                    +{item.combo_items.length - 2} more
                  </button>
                )}
              </div>
            )}

            {/* Expanded Details - Compact */}
            {showDetails && item.is_combo && (
              <div className="mb-2 p-2 bg-blue-50 rounded text-xs">
                <div className="flex flex-wrap gap-1">
                  {item.combo_items.slice(2).map((comboItem, index) => (
                    <span key={index} className="text-blue-800">
                      {comboItem.item_name} ×{comboItem.quantity}
                    </span>
                  ))}
                </div>
              </div>
            )}

            {/* Metadata - Single Line */}
            <div className="text-xs text-gray-500">
              ID: {item.menu_item_id} • Added {new Date(item.created_at).toLocaleDateString('en-GB')}
            </div>
          </div>

          {/* Right Side - Price and Actions */}
          <div className="flex items-center gap-3">
            {/* Compact Price */}
            <div className="text-right">
              <div className={`text-xl font-bold ${item.is_combo ? 'text-blue-600' : 'text-orange-600'}`}>
                £{item.default_price.toFixed(2)}
              </div>
              {item.is_combo && (
                <div className="text-xs text-gray-500">
                  {item.combo_items ? item.combo_items.length : 0} items
                </div>
              )}
            </div>

            {/* Compact Action Buttons */}
            <div className="flex items-center gap-1">
              <button
                onClick={() => onAddToMenu(item)}
                className={`px-3 py-1.5 text-white rounded text-sm font-medium transition-colors ${
                  item.is_combo 
                    ? 'bg-blue-600 hover:bg-blue-700' 
                    : 'bg-orange-600 hover:bg-orange-700'
                }`}
              >
                Add to Menu
              </button>

              <button
                onClick={() => onEdit(item)}
                className="p-1.5 text-gray-400 hover:text-blue-600 hover:bg-blue-50 rounded transition-colors"
                title="Edit"
              >
                <Edit className="h-4 w-4" />
              </button>

              <button
                onClick={() => onDelete(item)}
                className="p-1.5 text-gray-400 hover:text-red-600 hover:bg-red-50 rounded transition-colors"
                title="Delete"
              >
                <Trash2 className="h-4 w-4" />
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default VerticalCatalogGrid;
