import React, { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';
import { menuService } from '../services/menu';
import { useAuth } from '../context/AuthContext';
import { 
  Plus, 
  Search, 
  Edit, 
  Trash2, 
  DollarSign, 
  Package,
  Filter,
  X,
  Save,
  Settings,
  AlertCircle
} from 'lucide-react';
import toast from 'react-hot-toast';

const MenuCatalogPage = () => {
  const { user } = useAuth();
  const [catalogItems, setCatalogItems] = useState([]);
  const [categories, setCategories] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showForm, setShowForm] = useState(false);
  const [editingItem, setEditingItem] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [formLoading, setFormLoading] = useState(false);

  const { register, handleSubmit, reset, formState: { errors }, setValue } = useForm();

  useEffect(() => {
    loadCatalogData();
  }, [searchTerm, selectedCategory]);

  const loadCatalogData = async () => {
    try {
      setLoading(true);
      const params = {};
      if (searchTerm) params.search = searchTerm;
      if (selectedCategory) params.category = selectedCategory;

      const [itemsData, categoriesData] = await Promise.all([
        menuService.getCatalogItems(params),
        menuService.getCatalogCategories()
      ]);

      setCatalogItems(itemsData);
      setCategories(categoriesData);
    } catch (error) {
      toast.error('Failed to load catalog data');
      console.error('Error loading catalog:', error);
    } finally {
      setLoading(false);
    }
  };

  const onSubmit = async (data) => {
    setFormLoading(true);
    try {
      const catalogData = {
        item_name: data.item_name,
        description: data.description || null,
        default_price: parseFloat(data.default_price),
        category: data.category || null
      };

      if (editingItem) {
        await menuService.updateCatalogItem(editingItem.menu_item_id, catalogData);
        toast.success('Menu item updated successfully!');
      } else {
        await menuService.createCatalogItem(catalogData);
        toast.success('Menu item created successfully!');
      }

      setShowForm(false);
      setEditingItem(null);
      reset();
      loadCatalogData();
    } catch (error) {
      const errorMessage = error.response?.data?.detail || 'Failed to save menu item';
      toast.error(errorMessage);
    } finally {
      setFormLoading(false);
    }
  };

  const handleEdit = (item) => {
    setEditingItem(item);
    setValue('item_name', item.item_name);
    setValue('description', item.description || '');
    setValue('default_price', item.default_price);
    setValue('category', item.category || '');
    setShowForm(true);
  };

  const handleDelete = async (item) => {
    if (!window.confirm(`Are you sure you want to delete "${item.item_name}"?`)) {
      return;
    }

    try {
      await menuService.deleteCatalogItem(item.menu_item_id);
      toast.success('Menu item deleted successfully!');
      loadCatalogData();
    } catch (error) {
      toast.error('Failed to delete menu item');
    }
  };

  const handleCancel = () => {
    setShowForm(false);
    setEditingItem(null);
    reset();
  };

  const groupedItems = catalogItems.reduce((groups, item) => {
    const category = item.category || 'Uncategorized';
    if (!groups[category]) {
      groups[category] = [];
    }
    groups[category].push(item);
    return groups;
  }, {});



  return (
    <div className="min-h-screen bg-gray-50">
      <div className="container mx-auto px-4 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">Menu Catalog</h1>
          <p className="text-gray-600">Manage your standard menu items and pricing</p>
        </div>

        {/* Controls */}
        <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
          <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-4">
            {/* Search and Filter */}
            <div className="flex flex-col sm:flex-row gap-4 flex-1">
              <div className="relative flex-1">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
                <input
                  type="text"
                  placeholder="Search menu items..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-orange-500 focus:border-orange-500"
                />
              </div>

              <div className="relative">
                <Filter className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
                <select
                  value={selectedCategory}
                  onChange={(e) => setSelectedCategory(e.target.value)}
                  className="pl-10 pr-8 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-orange-500 focus:border-orange-500 min-w-[150px]"
                >
                  <option value="">All Categories</option>
                  {categories.map(category => (
                    <option key={category} value={category}>{category}</option>
                  ))}
                </select>
              </div>
            </div>

            {/* Add Button */}
            <button
              onClick={() => setShowForm(true)}
              className="inline-flex items-center px-4 py-2 bg-orange-600 text-white rounded-md hover:bg-orange-700 transition-colors"
            >
              <Plus className="h-4 w-4 mr-2" />
              Add Menu Item
            </button>
          </div>
        </div>

        {/* Add/Edit Form */}
        {showForm && (
          <div className="bg-white rounded-lg shadow-sm p-6 mb-6">
            <div className="flex justify-between items-center mb-6">
              <h2 className="text-xl font-semibold text-gray-900">
                {editingItem ? 'Edit Menu Item' : 'Add New Menu Item'}
              </h2>
              <button
                onClick={handleCancel}
                className="p-2 hover:bg-gray-100 rounded-md"
              >
                <X className="h-5 w-5" />
              </button>
            </div>

            <form onSubmit={handleSubmit(onSubmit)} className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Item Name *
                  </label>
                  <input
                    {...register('item_name', { 
                      required: 'Item name is required',
                      maxLength: { value: 100, message: 'Item name must be less than 100 characters' }
                    })}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-orange-500 focus:border-orange-500"
                    placeholder="e.g., Margherita Pizza"
                  />
                  {errors.item_name && (
                    <p className="mt-1 text-sm text-red-600">{errors.item_name.message}</p>
                  )}
                </div>

                <div>
                <div className="flex items-center justify-between mb-2">
                <label className="block text-sm font-medium text-gray-700">
                    Category
                </label>
                <button
                    type="button"
                    onClick={() => setShowCategoryManager(true)}
                    className="text-xs text-orange-600 hover:text-orange-700 flex items-center"
                >
                    <Settings className="h-3 w-3 mr-1" />
                    Manage
                </button>
                </div>
                <CategorySelector
                value={watch('category') || ''}
                onChange={(value) => setValue('category', value)}
                categories={categories}
                placeholder="Select or create category"
                error={errors.category?.message}
                />
            </div>
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-2">
                    Default Price (£) *
                  </label>
                  <div className="relative">
                    <DollarSign className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-5 w-5" />
                    <input
                      {...register('default_price', { 
                        required: 'Price is required',
                        min: { value: 0.01, message: 'Price must be greater than 0' },
                        pattern: {
                          value: /^\d+(\.\d{1,2})?$/,
                          message: 'Please enter a valid price (e.g., 12.99)'
                        }
                      })}
                      type="number"
                      step="0.01"
                      min="0.01"
                      className="w-full pl-10 pr-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-orange-500 focus:border-orange-500"
                      placeholder="0.00"
                    />
                  </div>
                  {errors.default_price && (
                    <p className="mt-1 text-sm text-red-600">{errors.default_price.message}</p>
                  )}
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Description
                </label>
                <textarea
                  {...register('description')}
                  rows={3}
                  className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-orange-500 focus:border-orange-500"
                  placeholder="Describe the dish, ingredients, allergens, etc."
                />
              </div>

              <div className="flex justify-end space-x-4 pt-4">
                <button
                  type="button"
                  onClick={handleCancel}
                  className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  disabled={formLoading}
                  className="inline-flex items-center px-4 py-2 bg-orange-600 text-white rounded-md hover:bg-orange-700 disabled:opacity-50"
                >
                  <Save className="h-4 w-4 mr-2" />
                  {formLoading ? 'Saving...' : editingItem ? 'Update Item' : 'Add Item'}
                </button>
              </div>
            </form>
          </div>
        )}

        {/* Catalog Items */}
        {loading ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {[...Array(6)].map((_, index) => (
              <div key={index} className="bg-white rounded-lg shadow-sm h-48 animate-pulse">
                <div className="p-6">
                  <div className="h-4 bg-gray-200 rounded mb-2"></div>
                  <div className="h-3 bg-gray-200 rounded mb-4 w-2/3"></div>
                  <div className="h-20 bg-gray-200 rounded"></div>
                </div>
              </div>
            ))}
          </div>
        ) : Object.keys(groupedItems).length > 0 ? (
          <div className="space-y-8">
            {Object.entries(groupedItems).map(([category, items]) => (
              <div key={category}>
                <h2 className="text-xl font-semibold text-gray-900 mb-4 flex items-center">
                  <Package className="h-5 w-5 mr-2 text-orange-600" />
                  {category}
                  <span className="ml-2 text-sm font-normal text-gray-500">
                    ({items.length} items)
                  </span>
                </h2>

                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                  {items.map(item => (
                    <div key={item.menu_item_id} className="bg-white rounded-lg shadow-sm hover:shadow-md transition-shadow">
                      <div className="p-6">
                        <div className="flex justify-between items-start mb-3">
                          <h3 className="text-lg font-semibold text-gray-900 flex-1">
                            {item.item_name}
                          </h3>
                          <div className="flex space-x-1 ml-2">
                            <button
                              onClick={() => handleEdit(item)}
                              className="p-1 text-gray-400 hover:text-blue-600 hover:bg-blue-50 rounded"
                              title="Edit item"
                            >
                              <Edit className="h-4 w-4" />
                            </button>
                            <button
                              onClick={() => handleDelete(item)}
                              className="p-1 text-gray-400 hover:text-red-600 hover:bg-red-50 rounded"
                              title="Delete item"
                            >
                              <Trash2 className="h-4 w-4" />
                            </button>
                          </div>
                        </div>

                        <div className="mb-4">
                          <span className="text-2xl font-bold text-orange-600">
                            £{parseFloat(item.default_price).toFixed(2)}
                          </span>
                        </div>

                        {item.description && (
                          <p className="text-gray-600 text-sm mb-4 line-clamp-3">
                            {item.description}
                          </p>
                        )}

                        <div className="text-xs text-gray-500">
                          ID: {item.menu_item_id}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className="text-center py-12">
            <Package className="h-16 w-16 text-gray-400 mx-auto mb-4" />
            <h3 className="text-lg font-medium text-gray-900 mb-2">
              {searchTerm || selectedCategory ? 'No items found' : 'No menu items yet'}
            </h3>
            <p className="text-gray-600 mb-4">
              {searchTerm || selectedCategory 
                ? 'Try adjusting your search or filter criteria.'
                : 'Start building your menu catalog by adding your first item.'
              }
            </p>
            {!searchTerm && !selectedCategory && (
              <button
                onClick={() => setShowForm(true)}
                className="inline-flex items-center px-4 py-2 bg-orange-600 text-white rounded-md hover:bg-orange-700"
              >
                <Plus className="h-4 w-4 mr-2" />
                Add Your First Item
              </button>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default MenuCatalogPage;
