import React, { useState, useEffect } from 'react';
import { menuService } from '../services/menu' // Adjust import path

const MenuPage = () => {
  const [menus, setMenus] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedDate, setSelectedDate] = useState('');

  // Load menus function
  const loadMenus = async () => {
    try {
      setLoading(true);
      setError(null);

      const data = await menuService.getScheduledMenus({ limit: 50 });

      // Handle the response - data should be an array of menu objects
      console.log('API Response:', data);

      // Set the menus data
      setMenus(data || []);

    } catch (error) {
      console.error('Error loading menus:', error);
      setError('Failed to load menus. Please try again.');
      setMenus([]); // Set empty array on error
    } finally {
      setLoading(false);
    }
  };

  // Load menus on component mount
  useEffect(() => {
    loadMenus();
  }, []);

  // Filter menus based on search and date
  const filteredMenus = menus.filter(menu => {
    const matchesSearch = menu.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesDate = selectedDate ? menu.menu_date === selectedDate : true;
    return matchesSearch && matchesDate;
  });

  // Handle adding a new menu (from your ScheduledMenuPage)
  const handleAddMenu = (newMenu) => {
    setMenus(prevMenus => [...prevMenus, newMenu]);
  };

  // Handle menu selection/ordering
  const handleOrderMenu = (menuId) => {
    const menu = menus.find(m => m.menu_id === menuId);
    if (menu && menu.orderlink) {
      // Navigate to order page or handle ordering
      window.location.href = menu.orderlink;
    }
  };

  // Calculate total items and price for a menu
  const getMenuSummary = (menu) => {
    const totalItems = menu.items?.length || 0;
    const totalPrice = menu.items?.reduce((sum, item) => sum + parseFloat(item.price || 0), 0) || 0;
    return { totalItems, totalPrice };
  };

  return (
    <div className="menu-page">
      <div className="menu-page-header">
        <h1>Browse Menus</h1>
        <p>Find the scheduled menu from your kitchen</p>
      </div>

      {/* Search and Filter Section */}
      <div className="search-filter-section">
        <div className="search-container">
          <input
            type="text"
            placeholder="Search menus..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="search-input"
          />
        </div>

        <div className="filter-container">
          <input
            type="date"
            value={selectedDate}
            onChange={(e) => setSelectedDate(e.target.value)}
            className="date-filter"
          />

          <button 
            onClick={() => {
              setSearchTerm('');
              setSelectedDate('');
            }}
            className="clear-filters-btn"
          >
            Clear Filters
          </button>
        </div>
      </div>

      {/* Loading State */}
      {loading && (
        <div className="loading-state">
          <p>Loading menus...</p>
        </div>
      )}

      {/* Error State */}
      {error && (
        <div className="error-state">
          <p className="error-message">{error}</p>
          <button onClick={loadMenus} className="retry-btn">
            Try Again
          </button>
        </div>
      )}

      {/* Menus Display */}
      {!loading && !error && (
        <div className="menus-section">
          {filteredMenus.length === 0 ? (
            <div className="no-menus-found">
              <div className="no-menus-icon">🍽️</div>
              <h3>No menus found</h3>
              <p>
                {menus.length === 0 
                  ? "No scheduled menus available at the moment." 
                  : "Try adjusting your search criteria."}
              </p>
              <button onClick={loadMenus} className="refresh-btn">
                Refresh Menus
              </button>
            </div>
          ) : (
            <div className="menus-grid">
              {filteredMenus.map((menu) => {
                const { totalItems, totalPrice } = getMenuSummary(menu);

                return (
                  <div key={menu.menu_id} className="menu-card">
                    <div className="menu-card-header">
                      <h3 className="menu-name">{menu.name}</h3>
                      <span className={`status-badge ${menu.active ? 'active' : 'inactive'}`}>
                        {menu.active ? 'Active' : 'Inactive'}
                      </span>
                    </div>

                    <div className="menu-details">
                      <div className="menu-date">
                        📅 {new Date(menu.menu_date).toLocaleDateString('en-GB', {
                          weekday: 'long',
                          year: 'numeric',
                          month: 'long',
                          day: 'numeric'
                        })}
                      </div>

                      <div className="menu-stats">
                        <span className="item-count">
                          🍽️ {totalItems} item{totalItems !== 1 ? 's' : ''}
                        </span>
                        <span className="total-price">
                          💰 £{totalPrice.toFixed(2)}
                        </span>
                      </div>
                    </div>

                    {/* Menu Items Preview */}
                    {menu.items && menu.items.length > 0 && (
                      <div className="menu-items-preview">
                        <h4>Menu Items:</h4>
                        <ul className="items-list">
                          {menu.items.slice(0, 3).map((item) => (
                            <li key={item.catalog_item_id} className="menu-item">
                              <span className="item-name">{item.item_name}</span>
                              <span className="item-price">£{parseFloat(item.price).toFixed(2)}</span>
                            </li>
                          ))}
                          {menu.items.length > 3 && (
                            <li className="more-items">
                              +{menu.items.length - 3} more items...
                            </li>
                          )}
                        </ul>
                      </div>
                    )}

                    <div className="menu-actions">
                      <button 
                        onClick={() => handleOrderMenu(menu.menu_id)}
                        className="order-btn"
                        disabled={!menu.active}
                      >
                        {menu.active ? 'Order Now' : 'Not Available'}
                      </button>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </div>
      )}

      {/* Debug Info (remove in production) */}
      {process.env.NODE_ENV === 'development' && (
        <div className="debug-info">
          <details>
            <summary>Debug Info (Dev Only)</summary>
            <pre>{JSON.stringify({ 
              totalMenus: menus.length, 
              filteredMenus: filteredMenus.length,
              searchTerm,
              selectedDate,
              loading,
              error 
            }, null, 2)}</pre>
          </details>
        </div>
      )}
    </div>
  );
};

export default MenuPage;
