// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import { AuthProvider } from './context/AuthContext';
import { CartProvider } from './context/CartContext';
import Header from './components/common/Header';
import Footer from './components/common/Footer';
import ProtectedRoute from './components/common/ProtectedRoute';
import MenuCatalogPage from './pages/MenuCatalogPage';
import ConnectionTest from './components/common/ConnectionTest';

// Pages
import HomePage from './pages/HomePage';
import LoginPage from './components/auth/Login';
import RegisterPage from './components/auth/Register';
import DashboardPage from './pages/DashboardPage';
import MenuPage from './pages/MenuPage';
import OrderPage from './pages/OrderPage';
import CartPage from './pages/CartPage';
import CheckoutPage from './pages/CheckoutPage';
import ProfilePage from './components/auth/Profile';

function App() {
  return (
    <AuthProvider>
      <CartProvider>
        <Router>
          <div className="min-h-screen flex flex-col">
            <Header />
            <main className="flex-1">
              <Routes>
                <Route path="/" element={<HomePage />} />
                <Route path="/login" element={<LoginPage />} />
                <Route path="/register" element={<RegisterPage />} />
                <Route path="/menu" element={<MenuPage />} />
                <Route path="/cart" element={<CartPage />} />
                <Route path="/profile" element={<ProtectedRoute><ProfilePage /></ProtectedRoute>} />
                <Route path="/orders" element={<ProtectedRoute><OrderPage /></ProtectedRoute>} />
                <Route path="/checkout" element={<ProtectedRoute><CheckoutPage /></ProtectedRoute>} />
                <Route path="/dashboard" element={<ProtectedRoute requiredRole="caterer"><DashboardPage /></ProtectedRoute>} />
                <Route path="/menu-catalog" element={
  <ProtectedRoute requiredRole="caterer">
    <MenuCatalogPage />
  </ProtectedRoute>
} />
              </Routes>
            </main>
            <Footer />
          </div>
          <Toaster position="top-right" />
        </Router>
      </CartProvider>
    </AuthProvider>
  );
}

export default App;
