import React from 'react';
import { Calendar, MapPin, Star, Clock } from 'lucide-react';
import { format } from 'date-fns';
import { useCart } from '../../context/CartContext';
import { useAuth } from '../../context/AuthContext';

const MenuCard = ({ menu }) => {
  const { addToCart } = useCart();
  const { isCaterer } = useAuth();

  const handleAddToCart = () => {
    addToCart(menu, 1);
  };

  const calculatePrice = () => {
    let total = 0;
    if (menu.items && typeof menu.items === 'object') {
      Object.values(menu.items).forEach(categoryItems => {
        if (Array.isArray(categoryItems)) {
          categoryItems.forEach(item => {
            if (item.price) {
              total += parseFloat(item.price);
            }
          });
        }
      });
    }
    return total;
  };

  const renderMenuItems = () => {
    if (!menu.items || typeof menu.items !== 'object') return null;

    return Object.entries(menu.items).map(([category, items]) => (
      <div key={category} className="mb-2">
        <h4 className="font-medium text-gray-800 capitalize">{category}:</h4>
        {Array.isArray(items) ? (
          <ul className="text-sm text-gray-600 ml-2">
            {items.map((item, idx) => (
              <li key={idx}>
                {typeof item === 'string' ? item : item.item_name}
                {item.price && <span className="float-right">£{item.price}</span>}
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-sm text-gray-600 ml-2">{items}</p>
        )}
      </div>
    ));
  };

  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow">
      {/* Header */}
      <div className="p-4 border-b">
        <div className="flex justify-between items-start">
          <h3 className="text-lg font-semibold text-gray-900">{menu.name}</h3>
          <p className="text-sm text-gray-600">Menu Date: {menu.menu_date}</p>
          <span className="text-lg font-bold text-orange-600">
            £{calculatePrice().toFixed(2)}
          </span>
          <span className={`px-2 py-1 text-xs rounded ${
            menu.active ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'
          }`}>
            {menu.active ? 'Active' : 'Inactive'}
          </span>
        </div>

        <div className="flex items-center text-sm text-gray-500 mt-2 space-x-4">
          <div className="flex items-center">
            <Calendar className="h-4 w-4 mr-1" />
            {format(new Date(menu.menu_date), 'MMM dd, yyyy')}
          </div>
          <div className="flex items-center">
            <Clock className="h-4 w-4 mr-1" />
            {menu.active ? 'Available' : 'Unavailable'}
          </div>
        </div>
      </div>

      {/* Menu Items */}
      <div className="p-4">
        {renderMenuItems()}
      </div>

      {/* Footer */}
      <div className="p-4 bg-gray-50 border-t">
        <div className="flex justify-between items-center">
          {menu.orderlink && (
            <a
              href={menu.orderlink}
              target="_blank"
              rel="noopener noreferrer"
              className="text-sm text-orange-600 hover:text-orange-700"
            >
              View Details
            </a>
          )}

          {!isCaterer && menu.active && (
            <button
              onClick={handleAddToCart}
              className="bg-orange-600 text-white px-4 py-2 rounded-md hover:bg-orange-700 transition-colors"
            >
              Add to Cart
            </button>
          )}
        </div>
      </div>
    </div>
  );
};

export default MenuCard;
